import { useRef, useCallback, useEffect } from 'react';
import { useIsomorphicLayoutEffect as useIsomorphicLayoutEffect$1 } from '../../utilities/use-isomorphic-layout-effect.js';

function KeypressListener({
  keyCode,
  handler,
  keyEvent = 'keyup'
}) {
  const tracked = useRef({
    handler,
    keyCode
  });
  useIsomorphicLayoutEffect$1(() => {
    tracked.current = {
      handler,
      keyCode
    };
  }, [handler, keyCode]);
  const handleKeyEvent = useCallback(event => {
    const {
      handler,
      keyCode
    } = tracked.current;

    if (event.keyCode === keyCode) {
      handler(event);
    }
  }, []);
  useEffect(() => {
    document.addEventListener(keyEvent, handleKeyEvent);
    return () => {
      document.removeEventListener(keyEvent, handleKeyEvent);
    };
  }, [keyEvent, handleKeyEvent]);
  return null;
}

